import React , { useEffect, useState} from "react";
import Calendar from 'react-calendar';
import IconButton from '@material-ui/core/IconButton';
import { AiFillBackward as ArrowBackIcon} from "react-icons/ai";
import { AiFillForward as ArrowForwardIosIcon} from "react-icons/ai";
import { GiConfirmed as DoneOutlineIcon} from "react-icons/gi";
import 'react-calendar/dist/Calendar.css';
import { makeStyles } from '@material-ui/core/styles';
import { isWithinInterval } from "date-fns";
import {useStore} from "react-redux";
import { useHistory } from "react-router-dom";
impo


function isWithinRange(date, range) {
    return isWithinInterval(date, { start: range[0], end: range[1] });
}
function isWithinRanges(date, ranges) {
    return ranges.some(range => isWithinRange(date, range));
}
const useStyles = makeStyles((theme) => ({
    root: {
      fontSize: 20,
      fontWeight: 'bold',
      color:'red',
    },
    backButton:{
      marginRight:210
    }
  }));

const disabledRanges = [
    [
        new Date(2021,2,19),
        new Date(2021,2,25),
    ],
    [
        new Date(2021,3,19),
        new Date(2021,3,25),
    ],
];
const  ParkingCalendar=()=>{
    const classes=useStyles();
    const store=useStore();
    const history=useHistory();
    const [dateValue, setDateValue] = useState([]);
    const [datesSelected,setDatesSelected]=useState(false);

    function back(){
        setDatesSelected(false);
    }
    function tileDisabled({ date, view }) {
        if (view === 'month') {
          return isWithinRanges(date, disabledRanges);
        }
    }
    function dateSelected(e){
        setDateValue([new Date(e[0]),new Date(e[1])]);
        setDatesSelected(true);
    }
    function book(){

    }
    useEffect(()=>{
        console.log(dateValue[0]);
        console.log(dateValue[1]);
    },[dateValue]);

    return(
        <div>
            {
                datesSelected ? 
                <div>
                    <p className={classes.root}>Details</p>
                    <h3>You have choosen to book</h3>
                    <h3>{showDatesRange(dateValue)}</h3>
                    <div style={{display:"flex",justifyContent:"space-between",marginTop:15}}>

                    <IconButton 
                        onClick={back}
                        variant="contained" 
                        color="primary" 
                        disableElevation 
                        >
                        <ArrowBackIcon  color="red"/> 
                    </IconButton> 
                    <IconButton 
                        //onClick={book}
                        variant="contained" 
                        color="primary" 
                        disableElevation 
                        >
                        <DoneOutlineIcon color="green"  onClick={book}/>
                    </IconButton>
                   </div>
                </div>:
                <Calendar
                    onChange={dateSelected}
                    tileDisabled={tileDisabled}
                    minDate={new Date((Date.now()))}
                    //value={dateValue}
                    view="month"
                    selectRange
                >

                </Calendar>

            }

        </div>
    )
}
export default ParkingCalendar;

function showDatesRange(dates){
    return `from ${dates[0].getDate()}-${dates[0].getMonth()+1}-${dates[0].getFullYear()} to 
     ${dates[1].getDate()}-${dates[1].getMonth()+1}-${dates[1].getFullYear()}
    `
}
