import React,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import styled from 'styled-components';
const useStyles = makeStyles((theme) => ({
    root:{
        textAlign:'center',
        justifyContent:'center'
    },
    progress: {
        width: '100px',
        backgroundColor:'grey'
    },
    bar: {
        width: '1%',
        height: '30px',
        backgroundColor: 'green',
      },
  }));
const Bar = styled.div`
background-color:${props=>props.nbBookedSeats<=2?'green':2<props.nbBookedSeats && props.nbBookedSeats <=3?'yellow':3<props.nbBookedSeats && props.nbBookedSeats<=4?'red':'grey'};
width:${props=>25*props.nbBookedSeats}px;
height:20px;
border:4px;
border-radius:15%;
text-align:center;
`;
const Status =()=>{
    const classes=useStyles();
    const [nbBookedSeats,setNbBookedSeats]=useState(4);
    return(
        <div>
            <h3>{nbBookedSeats<4? `There are ${ 4-nbBookedSeats} free for today `:'There are no free seats for today  but you can still book for an other day'}</h3>
            <div className={classes.progress}>
                <Bar nbBookedSeats={nbBookedSeats}><h3> {nbBookedSeats*25}%</h3></Bar>
            </div>
        </div>


    );
}

export default Status ;
