import { makeStyles } from '@material-ui/core/styles';
import styled from 'styled-components';
const useStyles = makeStyles((theme) => ({
    progress: {
        width: '100px',
        backgroundColor:'grey'
    },
    bar: {
        width: '1%',
        height: '30px',
        backgroundColor: 'green',
      },
  }));
const Bar = styled.div`
background-color:${props=>props.nbBookedSeats<=2?'greean':2 <props.nbBookedSeats<=3?'yellow':'red'};
width:${25*props.nbBookedSeats}

`;
const Status =()=>{
    const classes=useStyles();
    cons [nbBookedSeats,setNbBookedSeats]=useState(2);
    return(
        <div className={classes.progress}>
            <Bar nbBookedSeats={nbBookedSeats}></Bar>
        </div>
    );
}

export default Status ;
