import React, { useState,useEffect } from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import SlotTime from './SlotTime';

const useStyles = makeStyles((theme) => ({
  root: {
    fontSize: 20,
    fontWeight: 'bold',
    color:'red',
  },
}));

const Calendar1=()=>{
  const [dateValue, setDateValue] = useState(new Date());
  const [dateSelected, setDateSelected] = useState(false);
  const [timeSelected,setTimeSelected]=useState(false);
  const [dataCompleted,setDataCompleted]=useState(false);

  const classes=useStyles();
  function onDateSelcted(e){
    setDateValue(new Date(e));
    setDateSelected(true);
  }
  const backToCalendar=()=>{
    setDateSelected(false);
    setTimeSelected(false);
    setDataCompleted(false);

  }
  const showDetails=()=>{
    setDataCompleted(true);
  }
  return (
   <div >
     {dateSelected ? 
      ( <>
        dataCompleted?
          (

          <div style={{ margin:'0 auto'}}> 
            <p className={classes.root}>Pick a time</p>
            <SlotTime  setTimeSelected={setTimeSelected}/>
          </div>
          ):
          (<></>)

          <div style={{display:"flex",marginTop:15}}>
          <Button onClick={backToCalendar}
          variant="contained" 
          color="primary" 
          disableElevation 
          style={{marginLeft:10,marginRight:80,background:'black'}}
          >
          Back</Button> 
          {timeSelected&& <Button 
          onClick={showDetails}
          variant="contained" 
          color="primary" 
          disableElevation 
          style={{marginLeft:10,marginButtom:20,background:'green'}}>Book</Button> }
        </div>
        </>
      ):
      (<>
        <p className={classes.root}>Pick a date</p>
        <Calendar
          onChange={(e)=>onDateSelcted(e)}
          value={dateValue}
          minDate={new Date((Date.now()))}
        />
      </>
      )}
    </div>
  );
}
export default Calendar1;
