import React,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import styled from 'styled-components';
const useStyles = makeStyles((theme) => ({
    root:{
        textAlign:'center',
        justifyContent:'center',
        backgroundColor:'antiquewhite',
    },
    progress: {
        width: '100px',
        height: '20px',
        margin:'0 auto',
        border:'double'
    },
    bar: {
        width: '1%',
        height: '30px',
        backgroundColor: 'green',
      },
  }));
const Bar = styled.div`
background-color:${props=>props.nbBookedSeats<=2?'green':2<props.nbBookedSeats && props.nbBookedSeats <=3?'#FFA500':3<props.nbBookedSeats && props.nbBookedSeats<=4?'red':'grey'};
width:${props=>25*props.nbBookedSeats}px;
height:20px;
text-align:center;
`;
const Status =()=>{
    const classes=useStyles();
    const [nbBookedSeats,setNbBookedSeats]=useState(4);
    return(
        <div className={classes.root}>
            {nbBookedSeats<4?
            <h3>{ 4-nbBookedSeats==1? `There is one  free seat `: `there are ${ 4-nbBookedSeats} free seats `} for today</h3>
            :<><h2>There are no free seats for today</h2>  <h3>but you can still book for another day</h3></>
            }
            <div className={classes.progress}>
                <Bar nbBookedSeats={nbBookedSeats}><b> {nbBookedSeats*25}%</b></Bar>
            </div>
        </div>


    );
}

export default Status ;
