import React from "react";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
const disabledDates = [
    new Date(2021,4,1),
    new Date(2021,4,2),
];
const  ParkingCalendar=()=>{

    const disableDates=({date,view})=>{

        if(view === 'month')
            for ( let disabledDate of disabledDates){
                console.log(  `${date.getFullYear()}/ ${date.getMonth()}/${date.getDate()} vs ${disabledDate.getFullYear()}/ ${disabledDate.getMonth()}/${disabledDate.getDate()}`);

                const shoudBeDisabled=date.getFullYear() === disabledDate.getFullYear() &&
                date.getMonth() === disabledDate.getMonth() &&
                date.getDate() === disabledDate.getDate()

                return shoudBeDisabled;
            }
    }

    return(
        <div>
            <Calendar
                tileDisabled={disableDates}
                minDate={new Date((Date.now()))}
            ></Calendar>
        </div>
    )
}
export default ParkingCalendar;
