import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {getTime} from "../util/formatTime";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
    marginTop:20,
    border:'4px',
    textAlign:'left'
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

export default function SimpleCard({reservation,type}) {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;

  return (
    <Card className={classes.root}>
        {
        type==='seat'?
        <CardContent>
            <div variant="h5" component="h2" gutterBottom>
            {bull} <b>id of seat </b>: {reservation.id}
            </div>
            <div variant="h5" component="h5">
            {bull} <b>Created at : </b>{reservation.created_At}
            </div>
            <div variant="h5" component="h3">
            {bull} <b>Reservation date : </b>{reservation.created_At}
            </div>
            <div variant="h5" component="h3">
            {bull} <b>Reservation date : </b>{getTime(reservation.time)}
            </div>
      </CardContent>
      :<CardContent>
            <Typography className={classes.title} color="textSecondary" gutterBottom>
            Word of the Day
            </Typography>
            <Typography variant="h5" component="h2">
            be{bull}nev{bull}o{bull}lent
            </Typography>
            <Typography className={classes.pos} color="textSecondary">
            adjective
            </Typography>
            <Typography variant="body2" component="p">
            well meaning and kindly.
            <br />
            {'"a benevolent smile"'}
            </Typography>
      </CardContent>
      }
    </Card>
  );
}
